import React, { useEffect, useState } from 'react';
import axios from 'axios';
import LeagueCreationForm from './leaguecreation';
import './App.css';


const LeagueList = () => {
  const [leagues, setLeagues] = useState([]);

  useEffect(() => {
    axios.get('http://merlunietest.com/league/allleagues')
      .then(res => setLeagues(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <h2>Existing Leagues</h2>
      {leagues.map(league => <p key={league.id}>{league.league_name}</p>)}
    </div>
  );
};

const UserLeagues = () => {
  const [userLeagues, setUserLeagues] = useState([]);

  useEffect(() => {
    // You may need to adjust this URL based on your session/user authentication
    axios.get('http://merlunietest.com/league/user-leagues')
      .then(res => setUserLeagues(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div>
      <h2>Your Leagues</h2>
      <p>You have created {userLeagues.length} leagues.</p>
      {userLeagues.map(league => <p key={league.id}>{league.league_name}</p>)}
    </div>
  );
};

const App = () => {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Create a League</h1>
      </header>
      <LeagueCreationForm />
      <LeagueList />
      <UserLeagues />
    </div>
  );
};

export default App;
